package paquete;

public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// int y char
		int x;
		x = 2;
		switch(x)
		{
		case 0:
		{
			System.out.println("Esta es la opcion 0");
			break;
		}
		case 1:
		{
			System.out.println("Esta es la opcion 1");
			break;
		}
		default:
		{
			System.out.println("Esta opcion es la opcion por defecto");
		}
		}
	}

}
